  &reftitle.examples;



    Exemplo de tidy::parseFile


<?php
$tidy = new tidy();
$tidy->parseFile('file.html');

$tidy->cleanRepair();

if(!empty($tidy->errorBuffer)) {
    echo "Os seguintes erros ou avisos ocorreram:\n";
    echo $tidy->errorBuffer;
}
?>
